import React from 'react';
import ErrorBoundary from './errorHandler'
import { StyleSheet, Text, View,TextInput,Dimensions,TouchableOpacity } from 'react-native';



const TimeCountdownView = props =>{
	return (
		<View style={styles.workTimeContainer}>
			<Text>{props.countDownTimer.mins} : </Text>
		</View>
	)

}

const TimeView = props => {

	return (
		<View>

			<View style={styles.workTimeContainer}>
				<Text>Work Time</Text> 
			</View>
			<View style={styles.workTimeContainer}>
			<Text>Mins : </Text> 
			<TextInput style={styles.inputContainer} 
			onChangeText= {props.setWorkMins}
			value={props.workMins}
			/>
			<Text>Secs : </Text> 
				<TextInput style={styles.inputContainer} 
				onChangeText= {props.setWorkSecs}
				value={props.workSecs}
				/>
			</View>


			<View style={styles.workTimeContainer}>
				<Text>Break Time</Text> 
			</View>
			<View style={styles.workTimeContainer}>
			<Text>Mins : </Text> 
				<TextInput style={styles.inputContainer} 
				onChangeText= {props.setBreakMins}
				value={props.breakMins}
				/>
			<Text>Secs : </Text> 
				<TextInput style={styles.inputContainer} 
				onChangeText= {props.setBreakSecs}
				value={props.breakSecs}
				/>
		</View>
		</View>
	)

}

const ButtonView = props => {
	return (
		<View style={styles.workTimeContainer}>
			{ props.isTimerOn ? 
			<TouchableOpacity style={styles.buttonView}
			onPress = {props.stopTimer}>
			<Text>
				Pause
			</Text>
		</TouchableOpacity>
				: 
				<TouchableOpacity style={styles.buttonView}
					onPress = {props.startTimer} 
				>
					<Text>
						Start
					</Text>
				</TouchableOpacity>

			}

			<TouchableOpacity style={styles.buttonView}
				onPress = {props.resetTimer} >
				<Text>
					Reset
				</Text>
			</TouchableOpacity> 

		</View>
	)


}

export default class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = { 
			workMins: '25 ' ,
			workSecs:' ',
			breakMins: '10 ',
			breakSecs:' ',
			countDownTimer : {
				minutes : 0,
				seconds : 0
			},
			isTimerOn : false

		};
	}

	setWorkMins = (workMins) =>{
		this.setState({workMins});
	}
	setWorkSecs = (workSecs) =>{
		this.setState({workSecs});
	}

	setBreakMins = (breakMins) =>{
		this.setState({breakMins});
	}

	setBreakSecs = (breakSecs) =>{
		this.setState({breakSecs});
	}

	startTimer = () =>{
		this.setState({
			isTimerOn : true
		})



	}

	stopTimer = () => {

	}

	resetTimer = () =>{
		this.setState({
			isTimerOn : false
		})

	}

  	render() {
		return (
			<ErrorBoundary>
				<View style={styles.mainContainer}>

					<Text>Pomodoro Timer</Text>

					<TimeCountdownView  countDownTimer = {this.state.countDownTimer}/>

					<TimeView workMins = {this.state.workMins} 
						workSec ={this.state.workSecs}
						breakMins = {this.state.breakMins}
						breakSecs = {this.state.breakSecs}
						setBreakMins = {this.setBreakMins}
						setBreakSecs = {this.setBreakSecs}
						setWorkMins = {this.setWorkMins}
						setWorkSecs = {this.setWorkSecs}
					/>

					<ButtonView resetTimer = {this.resetTimer}
						stopTimer = {this.stopTimer}
						startTimer = {this.startTimer}
						isTimerOn = {this.state.isTimerOn}
					/>
				</View>
			</ErrorBoundary>

		);
  }
}

const styles = StyleSheet.create({
	workTimeContainer: {
		flexDirection: 'row',
		alignItems: 'center',
		paddingTop : 40
	},
	inputContainer :{
		height: 30, 
		width:100,
		borderColor: 'gray', 
		borderWidth: 1,
		marginRight : 5
	},
	mainContainer : {
		flex: 1,
		backgroundColor: '#fff',
		alignItems: 'center',
		paddingTop : 50
	},
	buttonView : {
		borderWidth : 5,
		width : 60,
		height : 60,
		borderRadius :30,
		alignItems: 'center',
		justifyContent : 'center',
		marginRight : 40,
		borderColor: 'red'

	},
	buttonText : {
		fontSize : 40,
		color: 'red',
	}



});
